import React, { useCallback, useState, useEffect, useRef} from 'react';
import { Form, Input, Button} from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import { ADD_POST_SUCCESS, ADD_POST_REQUEST, UPLOAD_IMAGES_REQUEST } from '../reducers/post';



const PostForm = () => {
    //const { imagePaths } = useSelector( state => state.post);
    const dispatch = useDispatch();
    const [ text, setText ] = useState('');
    const { imagePaths, isAddingPost, postAdded } = useSelector( state => state.post );
    const imageInput = useRef();

    useEffect( () => {
        if (postAdded) {
            setText('');
        }
    }, [postAdded]);


    const onSubmitForm = useCallback( (e) => {
        e.preventDefault();   ///  안 넣으면 새 페이지로 넘어감
        if (!text && !text.trim()) {
            return alert('게시글을 작성하세요');
        }

        const formData = new FormData();
        imagePaths.forEach((i) => {
        formData.append('image', i);
        });
        formData.append('content', text);

        dispatch({
            type: ADD_POST_REQUEST,
            data: {
                content: text.trim(),
            },
        });
        console.log('onSubmitForm() text : ', text);
    }, [text, imagePaths]);

    //const [id, setId ] = useState('');
    const onChangeText = useCallback( (e) => {
       // e.preventDefault();
        setText(e.target.value);
    }, []);

    const onChangeImages = useCallback( (e) => {
        console.log(e.target.files);
        const imageFormData = new FormData();
        [].forEach.call(e.target.files, (f) => {
            imageFormData.append( 'image', f);
        });
        dispatch({
            type: UPLOAD_IMAGES_REQUEST,
            data: imageFormData,
        });
    }, []);

    const onClickImageUpload = useCallback(() => {
        console.log("imageInput.current : ", imageInput.current);
        imageInput.current.click();
    }, [imageInput.current]);


    return (
        <Form style={{ margin: '10px 0 20px'  }} encType="multipart/form-data" onSubmit={onSubmitForm}>
            <Input.TextArea maxLength={140} placeholder="어떤 신기한 일이 있었나요?" value={text} onChange={onChangeText}/>
            <div>
                <input type="file" multiple hidden ref={imageInput} onChange={onChangeImages} />
                <Button onClick={onClickImageUpload}> 이미지 업로드</Button>
                <Button type="primary" style={{ float: 'right'}} htmlType="submit" isLoading={isAddingPost}>색색</Button>
            </div>
            <div>
                {imagePaths.map( v => (
                        <div key={v} style={{ display: 'inline-block'}}>
                            <img src={`http://localhsot:3065/uploads/${v}` } style={{ width: '200px' }} alt={v} />
                            <div>
                                <Button>제거</Button>
                            </div>
                        </div>
                    )
                )}
            </div>
        </Form>
    );
};

export default PostForm;
