import React, { useEffect, useCallback } from 'react';
import { Form, Button, List, Card, Icon, Input } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { LOAD_FOLLOWERS_REQUEST, LOAD_FOLLOWINGS_REQUEST, UNFOLLOW_USER_REQUEST, REMOVE_FOLLOWER_REQUEST } from '../reducers/user';
import { LOAD_USER_POSTS_REQUEST } from '../reducers/post';

const Profile = () => {
    const dispatch = useDispatch();
    const { me, followerList, followingList } = useSelector( state => state.user );
    const { mainPosts } = useSelector ( state => state.post );

    useEffect ( () => {

        dispatch({
            type: LOAD_FOLLOWERS_REQUEST,
            data: me.id,
        });
        dispatch({
            type: LOAD_FOLLOWINGS_REQUEST,
            data: me.id,
        });
        dispatch({
            type: LOAD_USER_POSTS_REQUEST,
            data: me.id,
        });
    }, [me && me.id ]);

    const onUnfollow = useCallback(userId => () => {
        dispatch({
            type: UNFOLLOW_USER_REQUEST,
            data: userId,
        });
    }, []);

    const onRemoveFollower = useCallback( useId => {
        dispatch({
            type: REMOVE_FOLLOWER_REQUEST,
            data: userId,
        });
    }, []);

    return (
    <div>
        <Form style={{ marginBottom: '20px', border: '1px solid #d9d9d9', padding: '20px'}}>
            <Input addonBefore="닉네임" />
            <Button type="primary">수정</Button>
        </Form>
        <List
            style={{ marginBottom: '20px' }}
            grid={{ gutter: 4, xs: 2, md: 3 }}
            size="small"
            header={<div> 팔로윙 목록 </div> }
            loadMore={<Button style={{ width: '100%' }}> 더 보기 </Button>}
            dataSource={followingList}
            renderItem={item => (
                <List.Item style={{ marginTop: '20px' }}>
                    <Card actions={[<Icon key='icon' type="stop" />]} onClick={onUnfollow(item.id)}>
                        <Card.Meta description={item.nickname} /></Card>
                </List.Item>
            )}
        />
        <List
            style={{ marginBottom: '20px' }}
            grid={{ gutter: 4, xs: 2, md: 3 }}
            size="small"
            header={<div> 팔로워 목록 </div> }
            loadMore={<Button style={{ width: '100%' }}> 더 보기 </Button>}
            dataSource={followerList}
            renderItem={item => (
                <List.Item style={{ marginTop: '20px' }}>
                    <Card actions={[<Icon key='icon' type="stop" />]}><Card.Meta description={item.nickname} /></Card>
                </List.Item>
            )}
        />
        <div> 내프로필 </div>
    </div>
    );
};

export default Profile;
