import React, { useState, useCallback, useEffect } from 'react';
import { Form, Input, Checkbox, Button } from 'antd';
import PropTypes from 'prop-types';
import { useDispatch, useSelector } from 'react-redux';
import Router from 'next/router';
import { SIGN_UP_REQUEST } from '../reducers/user'


const TextInput = ({ value }) => {
    return (
        <div>{value }</div> 
    )
};

TextInput.propTypes = {
    value: PropTypes.string,
}

export const useInput = (initValue = null ) => {
    const [value, setter] = useState(initValue);
    const handler = useCallback( (e) => {
        setter(e.target.value);
    }, []);
    return [value, handler];
};


const Signup = () => {

    const [id, setId ] = useState('');
    const [nick, setNick] = useState('');
    const [password, setPassword] = useState('');
    const [passwordCheck, setPasswordCheck] = useState('');
    const [term, setTerm] = useState(false);
    const [passwordError, setPasswordError] = useState(false);
    const [termError, setTermError] = useState(false);
    const dispatch = useDispatch();
    const { isSigningUp, me } = useSelector( state => state.user );

    useEffect( () => {
        console.log('cke 999999999999')
        //console.log('state : ', state);
        console.log('me, me.id : ', me)
        if(me){
            alert('로그인 했으니 메인페이지로 이동합니다 ')
            Router.push('/');
        }
    }, [me && me.id]);



    const onSubmit = useCallback( (e) => {
        e.preventDefault();
        if (password !== passwordCheck) {
            return setPasswordError(true);
        }
        if (!term) {
            return setTermError(true);
        }
        dispatch({
            type: SIGN_UP_REQUEST,
            data: {
                userId: id,
                password,
                nickname: nick,
            },
        })
    }, [id, nick, password, passwordCheck, term]);

    const onChangeId = (e) => {
        setId(e.target.value);
    };
    const onChangeNick = (e) => {
        setNick(e.target.value);
    };
    const onChangePassword = (e) => {
        setPassword(e.target.value);
    };
    const onChangePasswordChk = (e) => {
        setPasswordError(e.target.value !== password);
        setPasswordCheck(e.target.value);
    };   
    const onChangeTerm = (e) => {
        setTermError(false);
        setTerm(e.target.checked);
    };

    if (me) {
        return null;
    }

    return (
    <>
        <Form onSubmit={onSubmit} style={{ padding: 10 }}>
            <TextInput value="123" />
            <div>
                <label htmlFor="user-id">아이디</label>
                <br />
                <Input name="user-id"  value={id} required onChange={onChangeId} />
            </div>
            <div>
                <label htmlFor="user-nick">닉네임</label>
                <br />
                <Input name="user-nick" value={nick} required onChange={onChangeNick} />
            </div>
            <div>
                <label htmlFor="user-password">비밀번호</label>
                <br />
                <Input name="user-password" type="password" value={password} required onChange={onChangePassword} />
            </div>
            <div>
                <label htmlFor="user-password-check">비밀번호체크</label> 
                <br />
                <Input name="user-password-check" type="password" value={passwordCheck} required onChange={onChangePasswordChk} />
                {passwordError && <div style={{ color: 'red'}}>비밀번호가 일치하지 않습니다.</div>}
            </div>
            <div>
                <Checkbox name="user-term" checked={term} onChange={onChangeTerm}>약관에 동의합니다.</Checkbox>
                {termError && <div style={{ color: 'red'}}>약관에 동의하셔야 합니다.</div>}
            </div>
            <div style={{ marginTop: 10 }} >
                <Button type="primary" htmlType="submit" loading={isSigningUp}>가압하기</Button>
            </div>

        </Form>
    </>
    );
};

export default Signup;
